mapper.select:
SELECT * FROM (SELECT *, (@row := @row + 1) AS row_num FROM (SELECT a.*, c.nickname, @row := 0, IF(b.exper_praise_histroy_id IS NULL,0,1) is_like FROM experience_info a LEFT JOIN exper_praise_histroy b ON a.experience_id=b.experience_id AND b.member_no='${member_no}', member_info c WHERE a.member_no=c.member_no
AND (a.server_class_id1='${server_class_id}' OR a.server_class_id2='${server_class_id}' OR a.server_class_id3='${server_class_id}' OR a.server_class_id4='${server_class_id}' OR a.server_class_id5='${server_class_id}') ) a) a
WHERE row_num >= ${begin}
AND row_num <= ${end}
