mapper.select:
SELECT * FROM (SELECT a.*, c.nickname, d.nickname server_member, DATE_FORMAT(a.create_time ,'%y-%m-%d %h:%i:%s') create_time_str, CASE order_status WHEN 0 THEN '未支付' WHEN 1 THEN '已下单' WHEN 3 THEN '已完成' WHEN 5 THEN '已取消' WHEN 7 THEN '支付中' ELSE '未知' END order_status_tr FROM order_info a LEFT JOIN member_info d ON a.server_member_no = d.member_no, shop_server b, member_info c WHERE a.member_no = c.member_no AND a.shop_server_id = b.shop_server_id ) a where 1=1
and a.order_code = '${orderSearch.orderCode}'
and a.server_name = '${orderSearch.serverName}'
AND a.create_time >= #{orderSearch.startTime}
AND a.create_time <= #{orderSearch.endTime}
AND a.order_status = #{orderSearch.orderStatus}
ORDER BY a.create_time DESC

SELECT * FROM (SELECT a.*, b.order_code, b.pay_price, b.member_no, b.server_name, b.server_address, b.order_phone, b.order_nickname, c.nickname server_member, DATE_FORMAT( a.create_time, '%y-%m-%d %h:%i:%s' ) create_time_str, CASE a.refund_order_status WHEN 0 THEN '已申请' WHEN 1 THEN '同意' WHEN 3 THEN '拒绝' WHEN 5 THEN '退款完成' WHEN 6 THEN '售后关闭' ELSE '未知状态' END refund_order_status_tr FROM refund_order_info a, order_info b LEFT JOIN member_info c ON b.server_member_no = c.member_no WHERE a.order_id = b.order_id ) a where 1=1
and b.order_code = '${orderSearch.orderCode}'
and b.server_name = '${orderSearch.serverName}'
AND a.create_time >= #{orderSearch.startTime}
AND a.create_time <= #{orderSearch.endTime}
AND a.refund_order_status = #{orderSearch.refundOrderStatus}
ORDER BY a.create_time DESC
