mapper.select:
SELECT * FROM (SELECT a.*, @row := @row + 1 row_num FROM (SELECT a.*, b.server_name, b.appointment_time_start, b.appointment_time_end, b.order_code, b.server_address, @row := 0 FROM wallet_change_record a LEFT JOIN order_info b ON a.relation = b.order_id WHERE a.member_no = '${member_no}' AND a.record_status = 1 ORDER BY a.create_time desc) a) a
WHERE row_num >= ${begin}
AND row_num <= ${end}
